	menu_cg22_item_3_desc = "Вбийте $max_progress; юнітів Сніговика своєю командою на будь-якій складності.",--Kill $max_progress; Snowman units with your team on any difficulty.
	menu_cg22_item_4 = "Презентує Подрібнений",--Presents Shredded
	menu_cg22_item_4_desc = "Жертвувати $max_progress; на будь-якій складності.",--Sacrifice $max_progress; presents on any difficulty.
	menu_cg22_item_5 = "Вбито сніговиків",--Snowmen Killed
	menu_cg22_item_5_desc = "Вбийте $max_progress; Сніговиків зі своєю командою на будь-якій складності.",--Kill $max_progress; Snowmen with your team on any difficulty.
	menu_cg22_item_6 = "Подарунки захищені",--Presents Secured
	menu_cg22_item_6_desc = "Забезпечити принаймні $max_progress; на будь-якій складності.",--Secure at least $max_progress; presents on any difficulty.
	menu_cg22_personal_1 = "Снігова куля вбиває.",--Snowball Kills
	menu_cg22_personal_1_desc = "У грі A Criminal Carol вбийте загалом 100 ворогів сніжками на будь-якій складності.",--In A Criminal Carol, kill a total of 100 enemies with snowballs on any difficulty.
	menu_cg22_personal_2 = "Вбиває.",--Kills
	menu_cg22_personal_2_desc = "У грі A Criminal Carol вбийте загалом 500 ворогів з гвинтівки ''Полярна зірка'' на будь-якій складності.",--In A Criminal Carol, kill a total of 500 enemies with the North Star rifle on any difficulty.
	menu_cg22_personal_3 = "Вбито особливих ворогів",--Special Enemies Killed
	menu_cg22_personal_3_desc = "У грі A Criminal Carol вбийте загалом 150 особливих ворогів з гвинтівки ''Північна зірка'' на складності Overkill або вище.",--In A Criminal Carol, kill a total of 150 special enemies with the North Star rifle on Overkill difficulty or above.
	menu_cg22_post_objective_1 = "Вбиває.",--Kills
	menu_cg22_post_objective_1_desc = "Убийте 2000 ворогів з будь-якої снайперської гвинтівки.",--Kill 2000 enemies with any sniper rifle.
	menu_cg22_post_objective_2 = "Вбито маскувальників",--Cloakers Killed
	menu_cg22_post_objective_2_desc = "Убийте 150 плащів з гвинтівки ''Північна зірка'' на дуже високій складності або вище.",--Kill 150 Cloakers with the North Star rifle on Very Hard difficulty or above.
	menu_cg22_post_objective_3 = "Подвійні вбивства",--Double Kills
	menu_cg22_post_objective_3_desc = "Виконайте 1000 подвійних вбивств з гвинтівки ''Північна зірка'' на будь-якій складності.",--Perform 1000 double kills with the North Star rifle on any difficulty.
	menu_cg22_post_objective_4 = "Здобич у безпеці.",--Loot Secured
	menu_cg22_post_objective_4_desc = "Здобути 20 мішків зі здобиччю (з подальшою успішною втечею) на будь-якій складності.",--Secure 20 loot bags (followed by a successful escape) on any difficulty.
	menu_cg22_post_objective_5 = "Вбито бульдозеристів",--Dozers Killed
	menu_cg22_post_objective_5_desc = "Убийте 40 дозаторів на рівні складності ''Надмірне вбивство'' або вище.",--Kill 40 Dozers on Overkill difficulty or above.
	menu_cg22_post_objective_6 = "Здобич у безпеці.",--Loot Secured
	menu_cg22_post_objective_6_desc = "Здобути 100 мішків здобичі (з подальшою успішною втечею) на будь-якій складності.",--Secure 100 loot bags (followed by a successful escape) on any difficulty.
	menu_cg22_post_objective_7 = "Здобич у безпеці.",--Loot Secured
	menu_cg22_post_objective_7_desc = "Здобути 250 мішків здобичі (з подальшою успішною втечею) на будь-якій складності.",--Secure 250 loot bags (followed by a successful escape) on any difficulty.
	menu_cg22_post_objective_8 = "Вбито бульдозеристів",--Dozers Killed
	menu_cg22_post_objective_8_desc = "Убийте 200 дозаторів на складності Overkill або вище.",--Kill 200 Dozers on Overkill difficulty or above.
	menu_cg22_post_objective_9 = "Здобич у безпеці.",--Loot Secured
	menu_cg22_post_objective_9_desc = "Здобути 500 мішків здобичі (з подальшою успішною втечею) на будь-якій складності.",--Secure 500 loot bags (followed by a successful escape) on any difficulty.
	menu_challenge_about_to_expire = "Термін дії закінчується менш ніж за 1 годину!",--Expires in less than 1 hour!
	menu_challenge_about_to_expire_extended = "ДО ЗАКІНЧЕННЯ ТЕРМІНУ ДІЇ",--ABOUT TO EXPIRE
	menu_challenge_any_100_headshot_kills = "Практика пострілу в голову",--Headshot Practice
	menu_challenge_any_100_headshot_kills_desc = "Точність і ефективність часто йдуть пліч-о-пліч з жорстоким милосердям. Ми, принаймні більшість з нас, не отримуємо задоволення від вимушеного знищення ворогів, тож чому б не полегшити їхні страждання швидким вбивством?",--Precision and effectiveness can often go hand-in-hand with brutal mercy. We, well most of us, don't take pleasure in the necessary downing of our enemies, so why not ease their suffering with a quick kill?
	menu_challenge_any_100_headshot_kills_obj = "Убийте 100 ворогів пострілами в голову, використовуючи зброю на свій вибір.",--Kill 100 enemies with headshots using weapons of your choice.
	menu_challenge_any_25_shield_kills = "Побудуйте пліт зі щитів",--Build a raft of shields
	menu_challenge_any_25_shield_kills_desc = "Убийте 25 щитів, використовуючи зброю на ваш вибір.",--Kill 25 shields using weapons of your choice.
	menu_challenge_any_25_sniper_kills = "Снайперіно",--Sniperino
	menu_challenge_any_25_sniper_kills_desc = "Убийте 25 снайперів, використовуючи зброю на вибір.",--Kill 25 snipers using weapons of your choice.
	menu_challenge_any_25_spooc_kills = "У темряву",--Into the Dark
	menu_challenge_any_25_spooc_kills_desc = "Убийте 25 маскувальників, використовуючи зброю на свій вибір.",--Kill 25 Cloakers using weapons of your choice.
	menu_challenge_any_25_tank_kills = "Бульдозер",--Bulldozer
	menu_challenge_any_25_tank_kills_desc = "Убийте 25 бульдозерів, використовуючи зброю на свій вибір.",--Kill 25 Bulldozers using weapons of your choice.
	menu_challenge_any_25_taser_kills = "Змащена блискавка",--Greased Lightning
	menu_challenge_any_25_taser_kills_desc = "Убийте 25 тазерів, використовуючи зброю на свій вибір.",--Kill 25 tasers using weapons of your choice.
	menu_challenge_any_300_kills = "Масовий вбивця.",--Mass murderer
	menu_challenge_any_300_kills_desc = "Убийте 300 ворогів з обраної вами зброї.",--Kill 300 enemies with the weapons of your choice.
	menu_challenge_any_50_headshot_kills = "У голову!",--In the head!
	menu_challenge_any_50_headshot_kills_desc = "Є одне місце, яке, як відомо, б'є найболючіше... Убийте 50 ворогів пострілами в голову, використовуючи зброю на свій вибір.",--There's that one spot where you know it hurts the most... Kill 50 enemies with headshots using weapons of your choice.
	menu_challenge_any_6_jobs = "Рекордно низький рівень злочинності",--Record low crime rate
	menu_challenge_any_6_jobs_desc = "Надходять повідомлення про те, що рівень злочинності є найнижчим за останнє десятиліття. Давайте щось з цим зробимо, виконайте 6 завдань на будь-яку складність.",--Reports have come in that the crime rate is at its lowest point this decade. Let's do something about that, finish 6 jobs on any difficulty.
	menu_challenge_any_6_jobs_objective = "Виконані завдання: $progress;/$max_progress;",--Jobs completed: $progress;/$max_progress;
	menu_challenge_armored_1 = "Нам потрібен більший човен.",--We're Gonna Need a Bigger Boat
	menu_challenge_armored_1_desc = "Старий Захід помилявся. Найкраще грабувати потяг, коли він стоїть на місці. Менше поїздок, щоб обчистити його, і менше навантаження на коней, розумієте?",--The old west got it all wrong. The best way to rob a train is when it's standing still. Fewer trips to clean it out and less strain on the horses, y'know?
	menu_challenge_armored_1_objective = "На транспорті: Пограбуйте потяг, викрадіть і заберіть 20 мішків з боєприпасами.",--On the Transport: Train Heist, steal and secure 20 bags worth of ammo.
	menu_challenge_armored_2 = "Але зачекайте - це ще не все!",--But Wait - There's More!
	menu_challenge_armored_2_desc = "Ви не повірите, скільки всього можна знайти в броньованих автомобілях, якщо добре пошукати. Але ви маєте здивуватися, навіщо комусь перевозити креслення вантажівкою в наш час...",--You wouldn't believe the stuff you can find in armored cars if you look hard enough. But you gotta wonder why someone would ship plans by truck in this day and age...
	menu_challenge_armored_2_objective = "Для будь-якого пограбування броньованого транспорту знайдіть план пограбування поїзда.",--On any Armored Transport heist, find the plans for the Train Heist.
	menu_challenge_armored_3 = "Треба було обручку на нього надіти.",--You Should Have Put a Ring on It
	menu_challenge_armored_3_desc = "Золото на колесах. Хіба це не мило? Якщо вам вдасться зловити кілька штук, не забудьте привезти багато гарних мішечків блискучих цеглинок.",--Gold on wheels. Ain't it sweet? If you manage to catch wind of some, make sure you bring back many nice bags of the shiny bricks.
	menu_challenge_armored_3_objective = "Завершіть будь-яке пограбування броньованого транспорту, забравши щонайменше 9 мішків золота.",--Finish any Armored Transport heist, having secured at least 9 bags of gold.
	menu_challenge_art_ghost = "Привид галереї",--Phantom of the Gallery
	menu_challenge_art_ghost_desc = "Іноді найкращий спосіб довести, що такі злодії, як ми, можуть бути такими ж культурними, як і мистецькі сноби, - це забрати їхні коштовності по-доброму, без розбиття черепів.",--Sometimes the best way to prove that thieves like us can be just as cultured as art snobs is to take their precious items nice and quite-like, with no bashing of skulls.
	menu_challenge_art_ghost_obj = "У завданні ''Художня галерея'' викрасти всі позначені картини і піти непомітно, нікого не вбиваючи. Щоб виконати це завдання, ви повинні пройти всю гру від початку до кінця.",--In the Art Gallery job, steal all marked paintings and leave in stealth without killing anybody. To complete this challenge, you have to have played the heist from start to end.
	menu_challenge_assault_rifle_100_kills = "Практика стрільби по мішенях: Штурмова гвинтівка",--Target Practice: Assault Rifle
	menu_challenge_assault_rifle_100_kills_desc = "Убийте 100 ворогів, використовуючи лише штурмові гвинтівки.",--Kill 100 enemies using only assault rifles.
	menu_challenge_bain_4_jobs = "Досить!",--Bain it up!
	menu_challenge_bain_4_jobs_desc = "Бейн відчайдушно потребує вашої уваги. Виконайте 4 завдання Бейна на будь-яку складність.",--Bain is desperate for your attention. Finish 4 of Bain's jobs on any difficulty.
	menu_challenge_bain_4_jobs_objective = "Виконані завдання: $progress;/$max_progress;",--Jobs completed: $progress;/$max_progress;
	menu_challenge_bat_3 = "Культурні стерв'ятники",--Culture Vultures
	menu_challenge_bat_3_desc = "Деякі люди ніколи не задовольняються. Дайте їм діаманти, а вони все одно захочуть більше. Гейстери зазвичай належать до таких людей, чи не так?",--Some people are never satisfied. Give them diamonds, and they still want more. Heisters are usually that kind of people, aren't they?
	menu_challenge_bat_3_objective = "У завданні ''Діамант'' отримайте 10 мішків додаткової здобичі.",--In the Diamond job, secure 10 bags of additional loot.
	menu_challenge_bat_4 = "Дим і дзеркала",--Smoke and Mirrors
	menu_challenge_bat_4_desc = "Іноді важливо не лише бути тихим. Швидкість може бути не менш важливою для професіонала. Чим більша робота, тим вищий її престиж, якщо ви можете успішно поєднувати обидві ці якості.",--Sometimes, it's not all about being quiet. Speed can be just as important for a professional. The bigger the job, the higher the prestige if you can employ both successfully.
	menu_challenge_bat_4_objective = "У завданні ''Діамант'' викрадіть діамант і втечіть менш ніж за 10 хвилин.",--In the Diamond job, steal the Diamond and escape in under 10 minutes.
	menu_challenge_bat_6 = "Діаманти в необробленому вигляді",--Diamonds in the Rough
	menu_challenge_bat_6_desc = "Хаос і безлад можуть бути досить веселими - особливо, якщо ви є їх причиною. Ніколи не втрачайте шанс зіпсувати очікування ворогів...",--Chaos and mayhem can be rather fun- especially if you're the cause of it. Never pass up a chance to mess around with your enemies expectations...
	menu_challenge_bat_6_objective = "У завданні ''Діамант'' вам потрібно зібрати діамантовий пазл, потім цілеспрямовано увімкнути сигналізацію, викрасти діамант і втекти на рівні складності OVERKILL або вище.",--In the Diamond job, complete the diamond puzzle, then purposefully set off the alarm, steal the Diamond and escape on the OVERKILL difficulty or above.
	menu_challenge_berry_2 = "Чистий дім",--Clean House
	menu_challenge_berry_2_desc = "Коли ви проникаєте в глибокий, темний і секретний об'єкт, вам потрібно переконатися, що він не містить цінностей, коли прийде час повертатися до конспіративної квартири.",--When you infiltrate a deep, dark and secret facility, you need to make sure to leave it empty of valuables when it's time head back to the safe house.
	menu_challenge_berry_2_objective = "У завданні ''Під горою'' заховайте всю здобич зі сховищ і вершини гори у рятувальному вертольоті.",--In the Beneath the Mountain job, secure all of the loot from the vaults and mountain-top in the escape helicopter.
	menu_challenge_berry_3 = "Екіпаж коммандос",--Commando Crew
	menu_challenge_berry_3_desc = "Якщо кожен хейстер виконує свою частину роботи, то справу можна виконати дуже швидко. Давайте перевіримо, чи зможете ви виконати роботу з жорстокою точністю спецназівця.",--If every heister does his or her equal share, a job can get done very quickly. Let's see if you can pull off a job with the brutal precision of a commando.
	menu_challenge_berry_3_objective = "У завданні ''Під горою'' відкрийте 4 сховища зі здобиччю протягом 10 хвилин після початку пограбування на складності OVERKILL або вище.",--In the Beneath the Mountain job, open the 4 vaults that contain loot within 10 minutes of starting the heist on the OVERKILL difficulty or above.
	menu_challenge_big_deal = "Велика справа.",--Big Deal
	menu_challenge_big_deal_desc = "Слону дуже багато залежить від роботи з обрамленням кадру. Не залишайте місця для помилок. Обміняйте дев'ять картин.",--The Elephant has a lot riding on the Framing Frame job. Leave no room for error. Trade nine paintings.
	menu_challenge_big_deal_objective = "На 2-й день завдання ''Обрамлення рами'' обміняйте 9 картин.",--On day 2 of the Framing Frame job, trade 9 paintings.
	menu_challenge_bigbank_3 = "''Солодкі шістнадцять",--Sweet Sixteen
	menu_challenge_bigbank_3_desc = "Ви здивуєтеся, скільки може нести одна людина. Скільки поїздок до сховища ви можете витримати?",--You'd be surprised how much one person can carry. How many trips to the vault can you stand?
	menu_challenge_bigbank_3_objective = "Виконайте завдання ''Великий банк'', отримавши 16 захищених мішків.",--Complete The Big Bank job with 16 secured bags.
	menu_challenge_bigbank_4 = "12 сердитих хвилин",--12 Angry Minutes
	menu_challenge_bigbank_4_desc = "Коли спрацьовує будильник, ти злишся. Він гучний і дуже відволікає. А ще вона приносить пухнастість. Тому, коли він спрацьовує, заходьте і швидко виходьте.",--When the alarm goes off, you get pissed. It's loud, and ever so distracting. It also brings the fuzz. So when it goes off, get in and get out fast.
	menu_challenge_bigbank_4_objective = "У завданні ''Великий банк'' завершіть пограбування менш ніж за 12 хвилин на складності ''Важко'' або вище.",--In the Big Bank job, complete the heist in under 12 minutes on the Hard difficulty or above.
	menu_challenge_bigbank_5 = "Не приносьте спеку",--Don't Bring the Heat
	menu_challenge_bigbank_5_desc = "Якщо вони не знають, що ти там, вони не викличуть копів. Це робить втечу звідти набагато менш клопіткою справою.",--If they don't know you're there, they won't call the cops. That makes getting the hell out of there a lot less of a hassle.
	menu_challenge_bigbank_5_objective = "У завданні ''Великий банк'' завершіть пограбування, не спрацювавши сигналізацію.",--In the Big Bank job, complete the heist without triggering the alarm.
	menu_challenge_bob_4 = "Мисливець за сховищами",--Storage Hunter
	menu_challenge_bob_4_desc = "Слону завжди потрібно кілька зайвих доларів для його нечесних кампаній. Заберіть всю додаткову здобич зі сховищ у День виборів 2.",--The Elephant always needs a few extra dollars for his crooked campaigns. Grab all the additional loot in the storage bins in Election Day 2.
	menu_challenge_bob_4_objective = "На другий день завдання ''День виборів'' викрасти все зайве зі сховищ.",--On day 2 of the Election Day job, steal all of the extra loot in the storage bins.
	menu_challenge_bob_8 = "Гаряча лава 2.0",--Hot Lava 2.0
	menu_challenge_bob_8_desc = "Стоматолог приготував для вас незвичайне випробування. Він хоче побачити, як ви будете користуватися комп'ютером у день виборів (день 1), не вмикаючи будильник і не торкаючись землі.",--The Dentist has an unusual test for you. He wants to see you use the computers in Election Day (Day 1) without setting off the alarm or touching the ground.
	menu_challenge_bob_8_objective = "У день 1 завдання ''День виборів'' використовуйте комп'ютер непомітно, не торкаючись землі.",--On day 1 of the Election Day job, use the computer in stealth without ever touching the ground.
	menu_challenge_born_5 = "Повний газ",--Full Throttle
	menu_challenge_born_5_desc = "Коли Бейн чогось хоче, він іноді хоче цього швидко. Це один з таких випадків. Насправді, він хоче цього швидше, ніж зазвичай!",--When Bain wants something, he sometimes wants it fast. This is one of those times. In fact, he wants it faster than usual!
	menu_challenge_born_5_objective = "Виконайте завдання другого дня The Biker Heist за 120 секунд на складності OVERKILL або вище.",--Complete the second day of The Biker Heist job within 120 seconds on the OVERKILL difficulty or above.
	menu_challenge_brb_8 = "Все золото в Брукліні",--All the Gold in Brooklyn
	menu_challenge_brb_8_desc = "Якщо взяти до уваги все планування і роботу, пов'язану зі зломом сховища, то варто витягти з нього якомога більше золота, щоб воно того вартувало.",--When you take into account all the planning and work that comes with breaking into a vault, it's a good idea to pull as much gold out of it as possible to make it worthwhile.
	menu_challenge_brb_8_objective = "На Бруклінському банку Джоб втекти з принаймні 12 мішками золота на дуже важкій складності або вище.",--On the Brooklyn Bank Job escape with at least 12 bags of gold on the Very Hard Difficulty or above.
	menu_challenge_butcher_4_jobs = "Ти потрібен М'ясникові!",--The Butcher needs you!
	menu_challenge_butcher_4_jobs_desc = "М'ясник відчайдушно потребує вашої уваги. Виконайте 4 завдання М'ясника на будь-якій складності.",--The Butcher is desperate for your attention. Finish 4 of The Butcher's jobs on any difficulty.
	menu_challenge_butcher_4_jobs_objective = "Виконані завдання: $progress;/$max_progress;",--Jobs completed: $progress;/$max_progress;
	menu_challenge_cac_12 = "Я впевнений, що ніхто цього не чув.",--I'm Sure No One Heard That
	menu_challenge_cac_12_desc = "Це рідкісна майстерність, яка дозволяє їм зберігати холоднокровність і виконувати роботу навіть після того, як їхня присутність буде виявлена, а човен з ''Хлопцями в блакитному'' вже на шляху до них.",--It's a heister of rare skill that can keep their head cool and get the job done after their presence is revealed and a boatload of Boys in Blue are on their way.
	menu_challenge_cac_12_objective = "В ''Українському завданні'' завершіть пограбування непомітно після спрацьовування сигналізації металошукачів.",--On the Ukrainian Job, finish the heist in stealth after triggering the alarm with the metal detectors.
	menu_challenge_cac_21 = "Я можу зробити це швидше!",--I can do it faster!
	menu_challenge_cac_21_desc = "Коли зомбі гризуть твою дупу, мародерство не є пріоритетом для більшості. Але грабіжники не можуть зупинитися, коли перед ними відкриваються сейфи...",--When there are zombies chompin' for your ass, looting isn't a priority for most. But heisters can't stop themselves when there are safes to be opened...
	menu_challenge_cac_21_objective = "У завданні ''Проклята кімната вбивств'' відкрийте щонайменше 6 сейфів за один прохід на дуже високій складності або вище.",--In the Cursed Kill Room job, open at least 6 safes in a single run on the Very Hard difficulty or above.
	menu_challenge_cac_23 = "Я відчуваю себе Робіном Гудом!",--I Feel Like Robin Hood!
	menu_challenge_cac_23_desc = "Іноді можна потурати своєму егоїстичному задоволенню, показуючи ворогам, наскільки вищі ваші навички. Ніщо так не говорить ''Я можу поцілити в тебе'', як використання лука і стріл проти вогнепальної зброї.",--Sometimes, it's OK to indulge in the pure ego-driven satisfaction of showing your enemies just how superior your skills really are. Nothing says "I can aim the shit out of you" like using a bow and arrow against a firearm.
	menu_challenge_cac_23_objective = "Виконайте завдання ''Під прикриттям'' з кожним членом команди, використовуючи лук без жодних модифікацій та звичайні стріли на складності OVERKILL або вище.",--Complete the Undercover job with every crew member using a bow without any modifications and normal arrows on OVERKILL difficulty or above.
	menu_challenge_cane_4 = "Накачані та веселі",--Pumped Up and Jolly
	menu_challenge_cane_4_desc = "Є певна брутальна елегантність в універсальності рушниці дванадцятого калібру, незалежно від того, чи це двостволка, чи одностволка, чи однозарядна, чи автоматична. Екіпаж з однаковим спорядженням виводить цю елегантність на ще вищий рівень.",--There's a certain brutal elegance in the versatility of a twelve gauge shotgun, no matter if it's a double or single barreled, one-shot or auto. A crew with uniform equipment takes that elegance to an even higher level.
	menu_challenge_cane_4_objective = "Виконайте завдання ''Майстерня Санти'', коли всі гравці в команді використовують лише дробовики.",--Complete the Santa's Workshop job having all players in the crew only use shotguns.
	menu_challenge_cane_5 = "Тільки Санта приносить подарунки",--Only Santa Brings Gifts
	menu_challenge_cane_5_desc = "Хороша хейстер вчиться обходитися лише найнеобхіднішим. Якщо ви зможете виконати роботу без жодних вигадливих доповнень, ваша винагорода буде набагато солодшою.",--A good heister learns to get by with just the basics. If you can do the job without using any fancy extras, your reward will be that much sweeter.
	menu_challenge_cane_5_objective = "У завданні ''Майстерня Санти'' викрадіть 10 мішків зі здобиччю, не використовуючи жодного обладнання чи ресурсів гравця.",--In the Santa's Workshop job, secure 10 bags of loot without using any player equipment or assets.
	menu_challenge_caribbean_pirate = "Карибський пірат",--Caribbean Pirate
	menu_challenge_caribbean_pirate_desc = "Мендоза хочуть показати Кобрам, що означає грати у вищій лізі. Принесіть їм чотири пакети метамфетаміну на Щурах (день 2), але замість цього вкрадіть гроші.",--The Mendoza's want to show the Cobra's what it means to play in the big leagues. Take them four meth bags on Rats (Day 2), but steal the payment instead.
	menu_challenge_caribbean_pirate_objective = "На другий день завдання Щура принесіть додаткові пакети з метом, але замість цього вкрадіть свою оплату.",--On day 2 of the Rat job, bring additional meth bags to the trade, but steal your payment instead.
	menu_challenge_cash_drop = "Готівка",--Cash
	menu_challenge_cash_earned = "Витрачання зароблених грошей: $progress; / $max_progress;",--Spending cash earned: $progress; / $max_progress;
	menu_challenge_cee_1 = "Потурати злочинам",--Indulge in Crime
	menu_challenge_cee_1_desc = "Бувають випадки, коли хочеться просто вистрілити в Місяць і подивитися, що з цього вийде. Але що ви будете робити, коли туди потрапите? Ну, космос досить великий...",--There are occasions when you just want to shoot for the moon and see what happens. But what do you do when you get there? Well, space is pretty big...
	menu_challenge_cee_1_objective = "Досягніть 50-го рівня в ''Злочинному розгулі''.",--Reach Level 50 on a Crime Spree.
	menu_challenge_cee_3 = "Протиправні дії",--Unlawful Transgressions
	menu_challenge_cee_3_desc = "Ви коли-небудь перевіряли, чи є в трудових книжках записи про те, що хейстери досягли певних успіхів у роботі? Якщо ні, то, чорт забирай, має бути...",--Have you ever checked the record books if there's an entry for heisters reaching certain accolades on a job? If there isn't, there damn well should be...
	menu_challenge_cee_3_objective = "Досягніть 250-го рангу в ''Злочинному розгулі''.",--Reach rank 250 on a Crime Spree.
	menu_challenge_charliesierra_1 = "Каналізаційні щури",--Sewer Rats
	menu_challenge_charliesierra_1_desc = "Ми хочемо винести кожну сумку з готівкою з GO Bank якнайшвидше і якнайсильніше, але тримайтеся подалі від очей преси, щоб не потрапити в поле зору журналістів. Використовуйте тільки каналізацію.",--We want to haul every bag of cash out of the GO Bank on OVERKILL or harder, but stay out of the eye of any press choppers. Use the sewers only.
	menu_challenge_charliesierra_1_objective = "У завданні GO Bank завершіть пограбування, винісши всі мішки з готівкою через каналізацію на складності OVERKILL або вище.",--In the GO Bank job, complete the heist having secured all bags of cash via the sewers on the OVERKILL difficulty or above.
	menu_challenge_charliesierra_5 = "Всі яйця в одному кошику",--All Eggs in One Basket
	menu_challenge_charliesierra_5_desc = "Я хочу показати деяким потенційним новим контактам, наскільки ефективною може бути система skyhook. Йдіть до GO Bank і витягайте кожну сумку з готівкою за допомогою skyhook.",--I want to show some potential new contacts just how effective the skyhook system can be. Head to GO Bank, and extract every bag of cash with the skyhook.
	menu_challenge_charliesierra_5_objective = "У завданні GO Bank попросіть літак підняти всі сумки з готівкою за допомогою гака.",--In the GO Bank job, have the plane pick up all bags of cash with the skyhook.
	menu_challenge_charliesierra_6 = "Ми всі професіонали",--We Are All Professionals
	menu_challenge_charliesierra_6_desc = "Справжній професіонал доводить, що може виконати роботу і безшумно, і нелетальним способом. Принаймні, якщо йдеться про цивільне населення...",--The consummate professional proves that he can get the job done both silently, and non-lethally. At least as far as civvies are concerned...
	menu_challenge_charliesierra_6_objective = "У завданні GO Bank завершіть пограбування без спрацювання сигналізації та без вбивства цивільних на складності OVERKILL або вище.",--In the GO Bank job, complete the heist without the alarm going off and without killing any civilians on the OVERKILL difficulty or above.
	menu_challenge_choose_global_value = "Мітка зброї",--Weapon Tag
	menu_challenge_choose_weapon = "Зброя",--Weapon
	menu_challenge_choose_weapon_category = "Категорія зброї",--Weapon category
	menu_challenge_choose_weapon_mod = "Мод на зброю",--Weapon mod
	menu_challenge_claim_reward_title = "Вимагаю винагороду: Мод на випадкову зброю",--Claim reward: Random weapon mod
	menu_challenge_clone_1 = "Атака клонів - Бульдозер",--Attack of the clones - Bulldozer
	menu_challenge_clone_1_desc = "Бувають випадки, коли треба повертатися за сім'єю, незважаючи на ціну, незважаючи на опір. Це єдиний спосіб виправити ситуацію зі злочинністю.",--There are times you gotta go back for family, no matter the cost, no matter the opposition. It's the only way to right the scales of crime.
	menu_challenge_clone_1_obj = "Виконайте завдання ''Втеча з Гокстона'' на складності OVERKILL або вище, з активним мутатором ''Армія клонів - Бульдозери''. Щоб виконати це завдання, ви повинні пройти пограбування від початку до кінця.",--Complete the Hoxton Breakout job on the OVERKILL difficulty or above, with the Clone Army - Bulldozers mutator active. To complete this challenge, you have to have played the heist from start to end.
	menu_challenge_clone_2 = "Атака клонів - електрошокер",--Attack of the clones - Taser
	menu_challenge_clone_2_desc = "Візьміть свій 10-галонний капелюх і будьте готові прийняти удар електричним струмом заради команди.",--Go grab your 10-gallon hat and be ready to take some electrocution for the team.
	menu_challenge_clone_2_obj = "Виконайте завдання ''Велика нафта'' на складності OVERKILL або вище, з активним мутатором ''Армія клонів - електрошокери''. Щоб виконати це завдання, ви повинні пройти пограбування від початку до кінця.",--Complete the Big Oil job on the OVERKILL difficulty or above, with the Clone Army - Tasers mutator active. To complete this challenge, you have to have played the heist from start to end.
	menu_challenge_clone_3 = "Атака клонів - Щит",--Attack of the clones - Shield
	menu_challenge_clone_3_desc = "Поліція ховається за рухомою стіною, коли ти намагаєшся в неї стріляти - це так неспортивно.",--Police hiding behind a movable wall when you're trying to shoot at them is just so unsporting.
	menu_challenge_clone_3_obj = "Виконайте завдання ''Сторожові пси'' на складності OVERKILL або вище, з активним мутатором ''Армія клонів - Щити''. Щоб виконати це завдання, ви повинні пройти пограбування від початку до кінця.",--Complete the Watchdogs job on the OVERKILL difficulty or above, with the Clone Army - Shields mutator active. To complete this challenge, you have to have played the heist from start to end.
	menu_challenge_clone_4 = "Атака клонів - Cloaker",--Attack of the clones - Cloaker
	menu_challenge_clone_4_desc = "''Однією з найгірших доль було б постійно стикатися з хвилею за хвилею цих безчесних ворогів і тікати від них''.",--"One of the worst fates would be to constantly have to face and flee from wave after wave of the these dishonorable enemies."
	menu_challenge_clone_4_obj = "Виконайте завдання ''Тюремний кошмар'' на складності OVERKILL або вище, з активним мутатором ''Армія клонів - плащі''. Щоб виконати це завдання, ви повинні пройти пограбування від початку до кінця.",--Complete the Prison Nightmare job on the OVERKILL difficulty or above, with the Clone Army - Cloakers mutator active. To complete this challenge, you have to have played the heist from start to end.
	menu_challenge_clone_5 = "Атака клонів - Медик",--Attack of the clones - Medic
	menu_challenge_clone_5_desc = "Використовувати терміт, щоб розплавити шлях через перешкоду, ніколи не набридне. Це просто огидна забава. А от бити одних і тих же тупих копів знову і знову - так. Але треба йти до кінця. Ці хлопці просто так не здадуться.",--Using thermite to melt your way past an obstacle never gets old. That stuff is just nasty fun. Whackin' the same dumb-ass cops over and over does though. Still, gotta see it through to the end. These guys just won't stay down.
	menu_challenge_clone_5_obj = "Виконайте завдання Першого Світового Банку на складності OVERKILL або вище, з активним мутатором Армія клонів - Медик. Щоб виконати це завдання, ви повинні пройти пограбування від початку до кінця.",--Complete the First World Bank job on the OVERKILL difficulty or above, with the Clone Army - Medic mutator active. To complete this challenge, you have to have played the heist from start to end.
	menu_challenge_cookoff_mutated = "Екстремальна кулінарія",--Extreme Cooking
	menu_challenge_cookoff_mutated_desc = "''Я можу зварити приголомшливо смачний горщик чилі, але сумніваюся, що навіть мого ''П'ятисигнального Хабанеро'' буде достатньо, щоб зрівнятися з тим хімічним сп'янінням, з яким ти так любиш мати справу...''",--"I can brew up a smashingly tasty pot of chili you know, but I doubt even my Five-alarm Habanero Special would be enough to match that chemical intoxocation you are so fond of dealing with.."
	menu_challenge_cookoff_mutated_obj = "Виконайте завдання ''Втеча'', маючи щонайменше 6 активних мутантів. Щоб виконати це завдання, ви повинні пройти пограбування від початку до кінця.",--Complete the Cook Off job with at least 6 mutators active. To complete this challenge, you have to have played the heist from start to end.
	menu_challenge_cosmetic_drop = "Косметичні засоби",--Cosmetic Items
	menu_challenge_crime_spree_heists = "Швидкісний розгул",--Speedy Spree
	menu_challenge_crime_spree_heists_desc = "Під час шести пограбувань у цьому челенджі моє справжнє кохання подарувало мені багато зброї та грошей, а також чудовий злочинний розгул.",--On the six heists of this challenge, my true love gave to me, lots of guns and money, in a lovely Crime Spree.
	menu_challenge_crime_spree_heists_obj = "Здійсніть 6 пограбувань у ''Розгулі злочинності''. Щоб виконати це завдання, ви повинні пройти пограбування від початку до кінця.",--Complete 6 heists in the Crime Spree. To complete this challenge, you have to have played the heist from start to end.
	menu_challenge_critical_100_kills = "Прицільна стрільба: Критичні влучання",--Target Practice: Critical Hits
	menu_challenge_critical_100_kills_desc = "Боєприпасів, на жаль, іноді буває мало, тому важливо вміти ефективно знищувати ворога.",--Ammunition can, unfortunately, be scarce at times, so it's important to be able to take down the enemy effectively.
	menu_challenge_critical_100_kills_obj = "Убийте 100 ворогів, зробивши вбивчий удар критичним.",--Kill 100 enemies with the killing blow being a critical hit.
	menu_challenge_dah_8 = "Полювання на блакитні сапфіри",--Hunt for the Blue Sapphires
	menu_challenge_dah_8_desc = "Колекціонування може стати нездоровою одержимістю. Воно також може бути дуже прибутковим, якщо ви не втрачаєте себе в ньому. Зосередження на одній конкретній деталі, наприклад, на кольорі дорогоцінного каменю, часто може допомогти вам досягти мети.",--Collection can become an unhealthy obsession. It can also be a very profitable one as long as you don't lose yourself in it. Focusing on one particular detail, such as the color of a gem, can often help you reach a goal.
	menu_challenge_dah_8_objective = "У ''Пограбуванні діамантів'' знайдіть і заберіть кожен сапфір за межами сховища на складності OVERKILL або вище.",--On the Diamond Heist find and pick up every sapphire outside the vault on the OVERKILL difficulty or above.
	menu_challenge_daily_reward = "Гроші та збройовий мод для зброї на ваш вибір.",--Money and a weapon mod for a weapon of your choice.
	menu_challenge_dark_2 = "Примарний біг",--Ghost Run
	menu_challenge_dark_2_desc = "Деякі люди не можуть терпіти ховатися в тіні. Коли вас оточують таємні агенти, не дивно, що ви хочете виконати роботу і якнайшвидше забратися звідти геть.",--Some folks can't stand skulking around in the shadows. When you're surrounded by Murky agents, it's not surprising to want to get the job done and get the hell out of there ASAP.
	menu_challenge_dark_2_objective = "Виконайте завдання ''Туманна станція'' протягом 7 хвилин після його початку.",--Complete the Murky Station job within 7 minutes of starting it.
	menu_challenge_dark_3 = "Пацифіст",--The Pacifist
	menu_challenge_dark_3_desc = "Бувають випадки, коли ви просто не в настрої вбивати, але пограбування все одно має бути здійснене. То чому б не перетворити це на гру?",--There are times when you're just not in a killing mood, but the heist still has to get done. So why not make a game of it?
	menu_challenge_dark_3_objective = "Виконайте завдання ''Каламутна станція'', не вбивши нікого з членів екіпажу.",--Complete the Murky Station job without anyone in the crew killing anyone.
	menu_challenge_deer_7 = "Неможливо, цього не може бути. Так?",--Impossible, It Can't Be. Is It?
	menu_challenge_deer_7_desc = "Бог знає чому, але хтось готовий платити великі гроші за тост Альміра. Я не збираюся скаржитися. Іди до Білого Різдва і забери це звідти.",--God knows why, but someone is willing to pay big bucks for Almir's Toast. I'm not going to complain. Get into White Xmas and secure that out of there.
	menu_challenge_deer_7_objective = "У завданні ''Біле Різдво'' отримайте рідкісний тост Альміра.",--In the White Xmas job, secure the rare Almir's Toast.
	menu_challenge_dentist_4_jobs = "Прийом у стоматолога!",--Dentist appointment!
	menu_challenge_dentist_4_jobs_desc = "Стоматолог відчайдушно просить вашої уваги. Виконайте 4 завдання Дантиста на будь-яку складність.",--The Dentist is desperate for your attention. Finish 4 of The Dentist's jobs on any difficulty.
	menu_challenge_dentist_4_jobs_objective = "Виконані завдання: $progress;/$max_progress;",--Jobs completed: $progress;/$max_progress;
	menu_challenge_diamonds_are_forever = "Діаманти назавжди",--Diamonds Are Forever
	menu_challenge_diamonds_are_forever_desc = "Нам потрібно швидко вивезти коштовності, тому мені потрібно, щоб ви запакували і закріпили чотири сумки з ювелірної крамниці, не допустивши втечі на машині під заставу.",--We need to get the jewels out fast, so I need you to bag and secure four bags from the Jewelry Store without the escape car bailing.
	menu_challenge_diamonds_are_forever_objective = "У завданні ''Ювелірний магазин'' викрасти і закріпити 4 сумки, не від'їжджаючи з першого разу на машині евакуації.",--In the Jewelry store job, steal and secure 4 bags without the escape car leaving the first time.
	menu_challenge_div_cat_cf2017_jobs = "Crimefest 2017",--Crimefest 2017
