	menu_options_help = "НАЛАШТУЙТЕ ГРУ ПІД СЕБЕ ТА ДЛЯ ЗРУЧНОСТІ.",--Change your preferences and game settings.
	menu_optique_illusions = "Оптичні ілюзії",--Optic Illusions
