	menu_challenge_earn_2000k_offshore_cash_desc = "Earn 2 000 000 in offshore money.",
	menu_challenge_earn_250k_cash = "Earn some pocket money",
	menu_challenge_earn_250k_cash_desc = "Earn 250 000 in spendable cash.",
	menu_challenge_elephant_4_jobs = "Longing for attention",
	menu_challenge_elephant_4_jobs_desc = "The Elephant is desperate for your attention. Finish 4 of The Elephant's jobs on any difficulty.",
	menu_challenge_elephant_4_jobs_objective = "Jobs completed: $progress;/$max_progress;",
	menu_challenge_expire_time = "Expires in $time_left; hours!",
