	menu_drop_soap = "Counter-Strike",
	menu_drop_soap_beta = "Counterstrike",
	menu_drop_soap_beta_desc = "BASIC: ##$basic;##$NL;When charging your melee weapon you will counterattack enemies that try to strike you, knocking them down. The knockdown does not deal any damage.$NL;$NL;ACE: ##$pro;##$NL;You gain the ability to counter attack cloakers and their kicks.",
	menu_drop_soap_desc = "BASIC: ##$basic;##$NL;When charging your melee weapon you will counterattack enemies that try to strike you, knocking them down. The knockdown does not deal any damage.$NL;$NL;ACE: ##$pro;##$NL;You can now counterattack Cloakers.",
	menu_ecm_2x = "ECM Specialist",
	menu_ecm_2x_beta = "ECM Specialist",
	menu_ecm_2x_beta_desc = "BASIC: ##$basic;##$NL;You can now place ##$multibasic;## ECM jammers instead of just one.$NL;$NL;ACE: ##$pro;##$NL;The ECM jammer duration is increased by an additional ##$multipro;## and the ECM feedback duration lasts ##$multipro2;## longer. Pagers are delayed by the ECM jammer.",
	menu_ecm_2x_desc = "BASIC: ##$basic;##$NL;You can place ##$multibasic;## ECM jammers instead of just one.$NL;$NL;ACE: ##$pro;##$NL;The ECM jammer duration is increased by ##$multipro;## and the ECM feedback duration lasts ##$multipro2;## longer. The ECM jammer delays the pager response time as long as the jammer is active.",
	menu_ecm_booster = "ECM Overdrive",
	menu_ecm_booster_beta = "ECM Overdrive",
	menu_ecm_booster_beta_desc = "BASIC: ##$basic;##$NL;Your ECM jammer and feedback duration is increased by ##$multibasic;##.$NL;$NL;ACE: ##$pro;##$NL;Your ECM jammer can now also be used to open certain electronic doors.",
	menu_ecm_booster_desc = "BASIC: ##$basic;##$NL;Your ECM jammer duration is increased by ##$multibasic;##.$NL;$NL;ACE: ##$pro;##$NL;Your ECM jammer can also be used to open certain doors.",
	menu_ecm_feedback = "ECM Feedback",
	menu_ecm_feedback_desc = "BASIC: ##$basic;##$NL;You can interact with ECM jammers to cause a feedback loop. When interacted, the ECM jammer has a ##$multibasic;## chance to incapacitate enemies within ##$multibasic2;## meters radius every ##$multibasic3;## seconds.$NL;$NL;ECM feedback lasts ##$multibasic4;## seconds.$NL;$NL;ACE: ##$pro;##$NL;You will instantly interact with a ECM jammer and the ECM feedback duration is increased by ##$multipro;##.$NL;$NL;Your ECM jammer has a ##$multipro2;## chance every ##$multipro3;## minutes to recharge its feedback ability.",
	menu_eco_sentry_beta = "Eco Sentry",
	menu_eco_sentry_beta_desc = "BASIC: ##$basic;##$NL;The cost of deploying a sentry gun is reduced by ##$multibasic;##.$NL;$NL;ACE: ##$pro;##$NL;Your sentry guns gain ##$multipro;## increased health.",
