	pln_at1_end_01_01 = "Ха! Ви зробили це! Цей ювелiрний вирiб повинен коштувати чимало, чи не так?",
	pln_at1_end_01_02 = "Чудова робота, джентльмени!",
	pln_at1_end_01b_01 = "Чудова робота. Я зв'яжусь iз своïми контактами, щоб збути цi коштовностi. І подивлюсь, що зможу дiзнатися про той кресленик. Чекайте на звiстки.",
	pln_at1_end_02_01 = "Це багато грошей! Гарна робота!",
	pln_at1_end_02_02 = "Добре зроблено, джентльмени! Деякi важко заробленi грошi!",
	pln_at1_end_02b_01 = "Вiдмiнна робота. Ви витратите трохи цих грошей. Я подивлюсь, що зможу знайти на цьому кресленику.",
	pln_at1_end_03_01 = "Солодке, солодке золото! Чудова робота, всi!",
